import React, {useState, useRef} from "react";

function Form(props) {
	const [user, setUser] = useState({});
	const [alldata, setAllData] = useState({});
	const formEle = useRef(null);

	const handleDataUpdate = (key, value, ele) => {
		alert(ele.target.ele_name);
		// setUser({ ...user, key: value })
	}

	const onSubmit = (event) => {
		event.preventDefault();
		let allFormdata = formEle.form.serialize();
		alert("" , allFormdata);


	};

	return (
		<div>
			<form ref={formEle} onSubmit={onSubmit}>
				<label>
				FName:
				<input type="text" name="user[fname]" value={user.fname} ele_name="fname" onChange={e => handleDataUpdate('fname', e.target.value, ele)}/>
				</label>
				<label>
				LName:
				<input type="text" name="user[lname]" value={user.namlnamee} onChange={e => handleDataUpdate('lname', e.target.value)}/>
				</label>
				<button type='submit'>
				Submit
				</button>
			</form>
		</div>
	);
}

export default Form;
