//   return (

//   );
// }

export default function ThemeNesting(props) {
    const classes = useStyles();
    return (

            <ThemeProvider
            theme={{
                background: 'linear-gradient(45deg,#357a38 30%, #21CBF3 90%)',
                boxShadow: '0 3px 5px 2px rgba(70, 203, 243, .3)',
            }}
            >
                (<button type="button" className={classes.root} {...props}>
                    {props.name}
                </button>)
            </ThemeProvider>

    );
}
