import React, {useState, useRef} from "react";

function Form(props) {
	const [fname, setFname] = useState("");
	const [lname, setLname] = useState("");
	const [alldata, setAllData] = useState({});
	const formEle = useRef(null);

	const onSubmit = (event) => {
		event.preventDefault();
		let allFormdata = formEle.form.serialize();
		alert("" . allFormdata);


	};

	return (
		<div>
			<form ref={formEle} onSubmit={onSubmit}>
				<label>
				FName:
				<input type="text" name="name" onChange={(e)=>setFname(e.target.value)}/>
				</label>
				<label>
				LName:
				<input type="text" name="name" onChange={(e)=>setLname(e.target.value)}/>
				</label>
				<button type='submit'>
				Submit
				</button>
			</form>
		</div>
	);
}

export default Form;
