import React, {useEffect, useState} from "react";

function Form(props) {
	const [user, setUser] = useState({});
	const [allUsersData, setAllUsersData] = useState([]);
	const [singleUser, setSingleUser] = useState(-1);


	useEffect(() => {
        let allUsersData = [];
        try{
			if(window.localStorage.getItem('users'))
				allUsersData = window.localStorage.getItem('users');
			if(allUsersData.length && typeof allUsersData !== 'undefined')
			{
				allUsersData = JSON.parse(allUsersData);
				setAllUsersData(allUsersData);
			}

			if(allUsersData.length && window.localStorage.getItem('edit_user'))
			{
				let userEdit = JSON.parse(window.localStorage.getItem('edit_user'));
				if(userEdit>-1)
					setUser(allUsersData[userEdit]);
				setSingleUser(userEdit);
			}

		}
		catch(e){
			console.log("CAUGHT ERROR:Table", e);
		}
    },[]);

	const handleDataUpdate = (ele) => {
		setUser({ ...user, [ele.target.attributes.getNamedItem('ele_name').value]: ele.target.value });
	}

	const onSubmit = (event) => {
		event.preventDefault();
		if(!(user.fname && user.lname))
			return;
		if(singleUser<0)
			allUsersData.push(user);
		else
			allUsersData[singleUser] = user;
		window.localStorage.setItem('users', JSON.stringify(allUsersData));
	};

	return (
		<div>
			<form >
				<label>
				FName:
				<input type="text" name="user[fname]" value={user.fname?user.fname:""} ele_name="fname" onChange={handleDataUpdate}/>
				</label>
				<label>
				LName:
				<input type="text" name="user[lname]" value={user.lname?user.lname:""} ele_name="lname" onChange={handleDataUpdate}/>
				</label>
				<button type='submit' onClick={onSubmit}>
				Submit
				</button>
				<button type='submit' onClick={()=>{window.localStorage.setItem('edit_user', -1);setSingleUser(-1)}}>
				Addnew
				</button>
			</form>
		</div>
	);
}

export default Form;
