import React, { useEffect, useState } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

// function createData(name, calories, fat, carbs, protein) {
//   return { name, calories, fat, carbs, protein };
// }

// const rows = [
//   createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
//   createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
//   createData('Eclair', 262, 16.0, 24, 6.0),
//   createData('Cupcake', 305, 3.7, 67, 4.3),
//   createData('Gingerbread', 356, 16.0, 49, 3.9),
// ];

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
});

export default function CustomizedTables() {
  const classes = useStyles();
  const [user, setUser] = useState({});
  useEffect(() => {
    let allUsersData = [];
    try {
      if (window.localStorage.getItem("users"))
        allUsersData = window.localStorage.getItem("users");
      if (allUsersData.length && typeof allUsersData !== "undefined") {
        allUsersData = JSON.parse(allUsersData);
        // setAllUsersData(allUsersData);
        setUser(allUsersData);
      }
    } catch (e) {
      console.log("CAUGHT ERROR:Table", e);
    }
  }, []);

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Actions</StyledTableCell>
            <StyledTableCell>Name</StyledTableCell>
            <StyledTableCell align="right">Mobile</StyledTableCell>
            <StyledTableCell align="right">Email</StyledTableCell>
            <StyledTableCell align="right">Gender</StyledTableCell>
            <StyledTableCell align="right">Hobbies</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {user.length > 0
            ? user.map((row, index) => {
                return (
                  <StyledTableRow key={index}>
                    <StyledTableCell align="left">
                        <div>
                        <IconButton color="secondary" aria-label="delete">
                        <DeleteIcon />
                      </IconButton> 
                      <IconButton color='primary' aria-label="edit">
                        <EditIcon />
                      </IconButton>
                        </div>

                    </StyledTableCell>
                    <StyledTableCell component="th" scope="row">
                      {row.fname} {row.lname}
                    </StyledTableCell>

                    <StyledTableCell align="right">
                      {row.mobile}
                    </StyledTableCell>
                    <StyledTableCell align="right">{row.email}</StyledTableCell>
                    <StyledTableCell align="right">
                      {row.gender}
                    </StyledTableCell>
                    <StyledTableCell align="right">
                      {row.hobby.join(",")}
                    </StyledTableCell>
                  </StyledTableRow>
                );
              })
            : null}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
