import React, { useState, useEffect } from "react";
import Spinner from "../component/spinner";
import { useHistory } from "react-router-dom";
function AddDtata(props) {
  const [organizationName, setOrganizationName] = useState("");
  const [organizationShortName, setOrganizationShortName] = useState("");
  const [organizationURL, setOrganizationURL] = useState("");
  const [organizationLOGO, setOrganizationLOGO] = useState("");
  const [loading, setLoading] = useState(false);
  //   useEffect(() => {
  //     let token = "";
  //     if (window.localStorage.getItem("token"))
  //       token = window.localStorage.getItem("token");
  //     console.log("token", token);

  //   }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    let token = "";
    if (window.localStorage.getItem("token"))
      token = window.localStorage.getItem("token");
    console.log("token in add data", token);
    let data = await fetch(
      " http://122.170.12.63:90//api/Organization/addOrganization",
      {
        method: "POST",
        headers: new Headers({
            Authorization: "Bearer " + token,
          "Content-Type": "application/json",
        }),
        body: JSON.stringify({
          organizationName: organizationName,
          organizationShortName: organizationShortName,
          organizationURL: organizationURL,
          organizationLOGO: organizationLOGO,
        }),
      }
    )
      .then((res) => res.json())
      .then(async (result) => {
        console.log("data submitted for new record", result);
        if (!result.data && result.success == false) {
          alert(result.message);
        } else {
          props.history.push("/home");
        }
      })
      .catch((err) => {
        console.log("Error-->sendCred", err);
      });

    setLoading(false);
  };
  return (
    <form onSubmit={handleSubmit}>
      <label>
        organizationName:
        <input
          type="text"
          maxLength="100"
          value={organizationName}
          onChange={(e) => {
            setOrganizationName(e.target.value);
          }}
          required
        />
      </label>
      <label>
        organizationShortName
        <input
          type="text"
          maxLength="50"
          value={organizationShortName}
          onChange={(e) => {
            setOrganizationShortName(e.target.value);
          }}
          required
        />
      </label>
      <label>
        organizationURL:
        <input
          type="url"
          value={organizationURL}
          onChange={(e) => {
            setOrganizationURL(e.target.value);
          }}
        />
      </label>
      <label>
        organizationLOGO:
        <input
          type="text"
          value={organizationLOGO}
          onChange={(e) => {
            setOrganizationLOGO(e.target.value);
          }}
        />
      </label>

      {loading ? (
        <Spinner type={"Circle"} />
      ) : (
        <input type="submit" value="Add New Data" />
      )}
    </form>
  );
}

export default AddDtata;
